pragma solidity ^0.4.15;


library BadgeLibrary {

    /// @notice represents simple details about a earnable badge
    struct BFBadge {
        address issuer;
        string description;
        string name;
        string image;
        string version; 
        string json;
        uint index;
    }    

    /// @notice represents details of an issued badge 
    struct BFCredential {
        address issuer;
        string description;
        string name;
        string image;
        string version; 
        string json;
        uint expires;
        address recipient;
        bytes32 txKey;
    }

    function credentialTxKey(address _issuer, bytes _msgData, uint _nonce) constant public returns(bytes32 key) {
        return sha3(_issuer, _msgData, _nonce);
    }

    function getBadgeNameHash(string _badgename) constant public returns (bytes32 _hash) {
        return sha3(_badgename);
    }

    function getIntegrityHash(
        address _issuer, 
        string _description, 
        string _name, 
        string _image, 
        string _version, 
        address _recipient
    ) constant public returns(bytes32 _hash) 
    {
        return bytes32(
            sha3(
                _issuer, 
                _description, 
                _name, 
                _image, 
                _version, 
                _recipient
        ));
    }

    function extractAddress(bytes32 _hash, bytes _sig) constant returns(address _issuer) {
        bytes32 r;
        bytes32 s;
        uint8 v;

        if (_sig.length != 65)
          return 0;

        // The signature format is a compact form of:
        //   {bytes32 r}{bytes32 s}{uint8 v}
        // Compact means, uint8 is not padded to 32 bytes.
        assembly {
            r := mload(add(_sig, 32))
            s := mload(add(_sig, 64))

            // Here we are loading the last 32 bytes. We exploit the fact that
            // 'mload' will pad with zeroes if we overread.
            // There is no 'mload8' to do this, but that would be nicer.
            v := byte(0, mload(add(_sig, 96)))

            // Alternative solution:
            // 'byte' is not working due to the Solidity parser, so lets
            // use the second best option, 'and'
            // v := and(mload(add(sig, 65)), 255)
        }

        // albeit non-transactional signatures are not specified by the YP, one would expect it
        // to match the YP range of [27, 28]
        //
        // geth uses [0, 1] and some clients have followed. This might change, see:
        //  https://github.com/ethereum/go-ethereum/issues/2053
        if (v < 27)
          v += 27;

        if (v != 27 && v != 28)
            return 0;

        _issuer = ecrecover(_hash, v, r, s);
        return _issuer;
    }
}

